                                                    // Include config file
                                                    require_once "config.php";

                                                    // Check if session is active
                                                    if (session_status() == PHP_SESSION_NONE) {
                                                        session_start();
                                                    }

                                                    // Check if the teacher's session ID is set
                                                    if (isset($_SESSION['id'])) {
                                                        $teacher_id = $_SESSION['id'];

                                                        // Prepare a select statement
                                                        $sql = "SELECT M.ID, U.FIRST_NAME, U.LAST_NAME, M.DATE, M.IS_ACCEPTED
                                                                FROM MEETING M
                                                                INNER JOIN USER U ON U.ID = M.FK_PARENT
                                                                WHERE M.FK_PARENT = ?";
                                                        
                                                        if ($stmt = $mysqli->prepare($sql)) {
                                                            // Bind the session ID parameter to the prepared statement
                                                            $stmt->bind_param("i", $teacher_id);

                                                            // Execute the statement
                                                            if ($stmt->execute()) {
                                                                $result = $stmt->get_result();
                                                                
                                                                if ($result->num_rows > 0) {
                                                                    // Output data of each row
                                                                    while ($row = $result->fetch_assoc()) {
                                                                        echo "<tr>";
                                                                        echo "<td>" . $row["ID"] . "</td>";
                                                                        echo "<td>" . $row["FIRST_NAME"] . " " . $row["LAST_NAME"] . "</td>";
                                                                        echo "<td>" . $row["DATE"] . "</td>";
if ($row["IS_ACCEPTED"] == 0) {
    // Display button to update IS_ACCEPTED value
    echo  "<td>" . "<button class='btn btn-primary btn-sm' onclick='updateAcceptance(" . $row["ID"] . ")'>Update</button>";
} else {
    echo "<td>" . "ACCEPTED";
}
echo "</td>";
                                                                        echo "</tr>";
                                                                    }
                                                                } else {
                                                                    echo "<tr><td colspan='4'>No meetings found</td></tr>";
                                                                }
                                                            } else {
                                                                echo "Error executing the statement: " . $stmt->error;
                                                            }

                                                            // Close the statement
                                                            $stmt->close();
                                                        } else {
                                                            echo "Error preparing the statement: " . $mysqli->error;
                                                        }
                                                    } else {
                                                        echo "Teacher's session ID is not set.";
                                                    }

                                                    // Close connection
                                                    $mysqli->close();
                                                ?>
