    <script>
function updateAcceptance(id) {
    // Make AJAX request to update IS_ACCEPTED value
    fetch('update_acceptance.php?id=' + id, {
        method: 'POST',
        body: JSON.stringify({ is_accepted: true }),
        headers: {
            'Content-Type': 'application/json'
        }
    })
    .then(response => {
        if (response.ok) {
            // Reload the page after successful update
            location.reload();
        } else {
            console.error('Failed to update acceptance status');
        }
    })
    .catch(error => console.error('Error:', error));
}
</script>

